import { useToasts } from "@scalar/use-toasts";
const { addToast } = useToasts();
const useClipboard = () => {
  const copyToClipboard = (value) => {
    navigator.clipboard.writeText(value).then(() => {
      addToast({ title: "Copied to the clipboard" }, { timeout: 2e3 });
    });
  };
  return {
    copyToClipboard
  };
};
export {
  useClipboard
};
